import { useState, useEffect } from "react";

const StateConsole = () => {
    let [count, setCount] = useState(0);
    const onClickCountUp = () => {
        setCount(count++);
    };
    useEffect(() => {
        console.log("useStateConsole의 count는");
        console.log(count);
    }, []);
    console.log("useStateConsole의 실제 count는");
    console.log(count);

    return (
        <>
            <div>{count}</div>
            <button onClick={onClickCountUp}>추가</button>
        </>
    );
};

export default StateConsole;
