const UseState = () => {
    let state = true;

    const onPressTrue = () => {
        console.log("진실 함수 실행");
        console.log("진실입니다");
    };
    const onPressFalse = () => {
        console.log("거짓 함수 실행");

        console.log("거짓입니다");
    };
    const onPressChangeState = () => {
        state = !state;
    };
    return (
        <div>
            {state ? "진실" : "거짓"}
            <button
                onClick={() => {
                    console.log("클릭 버튼 눌림");
                    state ? onPressTrue : onPressFalse;
                }}
            >
                클릭
            </button>
            <button onClick={() => onPressChangeState}>변경</button>
        </div>
    );
};

export default UseState;
