import { useState, useEffect } from "react";

const StateConsole = () => {
    const [count, setCount] = useState(0);
    // const onClickCountUp = () => {
    //     setCount(count++);
    // }
    useEffect(() => {
        setCount(1);
        console.log("useStateConsole의 count는");
        console.log(count);
    }, []);
    console.log("useStateConsole의 실제 count는");
    console.log(count);

    return (
        <>
            <div>{count}</div>
            <button onClick={setCount(count++)}></button>
        </>
    );
};

export default StateConsole;
