import { useEffect, useState } from "react";

const UseEffect = () => {
    const [input, setInput] = useState();
    console.log("1");
    useEffect(() => {
        console.log("mount", input);
        return () => {
            console.log("unmount", input);
        };
    }, [input]);
    console.log("2");
    return (
        <input
            onChange={(e) => {
                setInput(e.target.value);
            }}
        ></input>
    );
};

export default UseEffect;
