import { useState, useEffect } from "react";

const StateConsole = () => {
    let [boolean, setBoolean] = useState(true);
    const onClickChangeBoolean = () => {
        setBoolean(!boolean);
        console.log("count의 함수 내 값은");
        console.log(boolean);
    };

    return (
        <>
            <div>{boolean}</div>
            <button onClick={onClickChangeBoolean}>변경</button>
        </>
    );
};

export default StateConsole;
