import { useRef } from "react";

function UseRefEx() {
    let nonRef;
    const inputRef = useRef(null);
    function onClickSubmit() {}
    return (
        <>
            <form>
                <input placeholder="useRef 미사용" ref={nonRef}></input>
                <input placeholder="useRef 사용" ref={inputRef}></input>
                <button>제출</button>
            </form>

            <div>
                <div>useRef 안 쓴 input: {nonRef}</div>
                <div>
                    useRef 쓴 input:{" "}
                    {inputRef.current.value ? inputRef.current.value : ""}
                </div>
            </div>
        </>
    );
}

export default UseRefEx;
