import { useEffect, useState } from "react";

const forceUpdate = () => {
    const [updateNum, setUpdateNum] = useState(0);
    setUpdateNum(updateNum + 1);
};

const UseEffectEx = () => {
    useEffect(() => {
        console.log("첫 렌더 시에만 실행");
    }, []);

    return (
        <>
            <div></div>
            <div></div>
            <div></div>
            <button onClick={forceUpdate}></button>
        </>
    );
};
