import { useState, useEffect } from "react";

const StateConsole = () => {
    let [count, setCount] = useState(0);
    const onClickCountUp = () => {
        setCount(count++);
        console.log("count의 함수 내 값은");
        console.log(count);
    };

    return (
        <>
            <div>{count}</div>
            <button onClick={onClickCountUp}>추가</button>
        </>
    );
};

export default StateConsole;
