import { useEffect, useState } from "react";

const UseEffectEx = () => {
    const [updateNum, setUpdateNum] = useState(0);

    useEffect(() => {
        console.log("첫 렌더 시에만 실행");
    }, []);
    const forceUpdate = () => {
        setUpdateNum(updateNum + 1);
    };
    return (
        <>
            <div></div>
            <div></div>
            <div></div>
            <button onClick={forceUpdate}></button>
        </>
    );
};
export default UseEffectEx;
